<?php//die(var_dump($custom_fields_leads));$leads = array(    'name'=>'от '.$data['name'],    'status_id' =>'16961551',    'price' => 0,    'tags' => $data['tags'],    'responsible_user_id'=> 1987768,    'custom_fields'=>array(        array(            'id'=>$custom_fields_leads['CITY'],            'values'=>array(                array(                    'value'=>$data['city'],                )            )        ),        array(            'id'=>$custom_fields_leads['MESSAGE'],            'values'=>array(                array(                    'value'=>$data['message'],                )            )        ),        array(            'id'=>$custom_fields_leads['utm_source'],            'values'=>array(                array(                    'value'=>$data['utm_source'],                )            )        ),        array(            'id'=>$custom_fields_leads['utm_campaign'],            'values'=>array(                array(                    'value'=>$data['utm_campaign'],                )            )        ),        array(            'id'=>$custom_fields_leads['utm_content'],            'values'=>array(                array(                    'value'=>$data['utm_content'],                )            )        ),        array(            'id'=>$custom_fields_leads['utm_term'],            'values'=>array(                array(                    'value'=>$data['utm_term'],                )            )        ),        array(            'id'=>$custom_fields_leads['product'],            'values'=>array(                array(                    'value'=>$data['product'],                )            )        ),        array(            'id'=>$custom_fields_leads['part'],            'values'=>array(                array(                    'value'=>$data['part'],                )            )        )        /*array(            'id'=>$custom_fields_leads['SOURCE'],            'values'=>array(                array(                    'value'=>'Сайт',                )            )        )*/    ));$set_leads['request']['leads']['add'][]=$leads;#Формируем ссылку для запроса$link='https://'.$subdomain.'.amocrm.ru/private/api/v2/json/leads/set';$curl=curl_init(); #Сохраняем дескриптор сеанса cURL#Устанавливаем необходимые опции для сеанса cURLcurl_setopt($curl,CURLOPT_RETURNTRANSFER,true);curl_setopt($curl,CURLOPT_USERAGENT,'amoCRM-API-client/1.0');curl_setopt($curl,CURLOPT_URL,$link);curl_setopt($curl,CURLOPT_CUSTOMREQUEST,'POST');curl_setopt($curl,CURLOPT_POSTFIELDS,json_encode($set_leads));curl_setopt($curl,CURLOPT_HTTPHEADER,array('Content-Type: application/json'));curl_setopt($curl,CURLOPT_HEADER,false);curl_setopt($curl,CURLOPT_COOKIEFILE,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_COOKIEJAR,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_SSL_VERIFYPEER,0);curl_setopt($curl,CURLOPT_SSL_VERIFYHOST,0);$out=curl_exec($curl); #Инициируем запрос к API и сохраняем ответ в переменную$code=curl_getinfo($curl,CURLINFO_HTTP_CODE);CheckCurlResponse($code);/** * Данные получаем в формате JSON, поэтому, для получения читаемых данных, * нам придётся перевести ответ в формат, понятный PHP */$Response=json_decode($out,true);$Response=$Response['response']['leads']['add'];$output_leads = [];foreach($Response as $v)	if(is_array($v))		$output_leads[] = $v['id'];return $output_leads;?>